import React, { useState, useRef, useEffect } from 'react';
import { Mic, Volume2, Loader2, MessageSquare, Send, Wifi, MessageCircle, CloudSun, DollarSign, Sprout, Bug } from 'lucide-react';

const KissanAI = () => {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [inputText, setInputText] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('hi-IN');
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState('');
  const [serverStatus, setServerStatus] = useState('connected');
  const [showChat, setShowChat] = useState(false);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const audioRef = useRef(null);

  const languages = [
    { code: 'en-IN', name: 'English', nativeName: 'IN English' },
    { code: 'hi-IN', name: 'Hindi', nativeName: '‡§π‡§ø‡§Ç‡§¶‡•Ä' },
    { code: 'pa-IN', name: 'Punjabi', nativeName: '‡®™‡©∞‡®ú‡®æ‡®¨‡©Ä' },
    { code: 'mr-IN', name: 'Marathi', nativeName: '‡§Æ‡§∞‡§æ‡§†‡•Ä' },
    { code: 'ta-IN', name: 'Tamil', nativeName: '‡Æ§‡ÆÆ‡Æø‡Æ¥‡Øç' },
    { code: 'te-IN', name: 'Telugu', nativeName: '‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å' },
    { code: 'bn-IN', name: 'Bengali', nativeName: '‡¶¨‡¶æ‡¶Ç‡¶≤‡¶æ' },
    { code: 'gu-IN', name: 'Gujarati', nativeName: '‡™ó‡´Å‡™ú‡™∞‡™æ‡™§‡´Ä' },
    { code: 'kn-IN', name: 'Kannada', nativeName: '‡≤ï‡≤®‡≥ç‡≤®‡≤°' },
    { code: 'ml-IN', name: 'Malayalam', nativeName: '‡¥Æ‡¥≤‡¥Ø‡¥æ‡¥≥‡¥Ç' },
  ];

  useEffect(() => {
    checkServerStatus();
  }, []);

  const checkServerStatus = async () => {
    try {
      const response = await fetch('http://localhost:5000/');
      setServerStatus(response.ok ? 'connected' : 'error');
    } catch (err) {
      setServerStatus('error');
    }
  };

  const quickActions = [
    { icon: CloudSun, title: 'Weather', subtitle: 'Forecast', color: 'from-orange-400 to-yellow-400' },
    { icon: DollarSign, title: 'Market', subtitle: 'Prices', color: 'from-green-400 to-emerald-400' },
    { icon: Sprout, title: 'Crop', subtitle: 'Advice', color: 'from-green-500 to-teal-500' },
    { icon: Bug, title: 'Pest', subtitle: 'Control', color: 'from-blue-400 to-cyan-400' },
  ];

  const startListening = async () => {
    setError('');
    audioChunksRef.current = [];

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: { channelCount: 1, sampleRate: 16000 }
      });

      mediaRecorderRef.current = new MediaRecorder(stream, {
        mimeType: 'audio/webm;codecs=opus'
      });

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) audioChunksRef.current.push(event.data);
      };

      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        await sendAudioToSTT(audioBlob);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current.start();
      setIsListening(true);
    } catch (err) {
      setError('Microphone access denied');
    }
  };

  const stopListening = () => {
    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
      setIsListening(false);
    }
  };

  const sendAudioToSTT = async (audioBlob) => {
    setIsProcessing(true);

    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'audio.webm');
      formData.append('language', selectedLanguage);

      const response = await fetch('http://localhost:5000/api/stt', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      if (data.success && data.transcript) {
        setInputText(data.transcript);
        setShowChat(true);
      } else {
        setError('Could not understand. Please try again.');
      }
    } catch (err) {
      setError('Speech recognition failed');
    } finally {
      setIsProcessing(false);
    }
  };

  const handleSendMessage = async () => {
    if (!inputText.trim()) return;

    const userMsg = { role: 'user', content: inputText };
    setMessages(prev => [...prev, userMsg]);
    setInputText('');

    await getAIResponse(userMsg.content);
  };

  const getAIResponse = async (userInput) => {
    setIsProcessing(true);

    try {
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          message: userInput,
          language: selectedLanguage,
          history: messages
        })
      });

      const data = await response.json();

      if (data.success && data.response) {
        const aiMsg = { role: 'assistant', content: data.response };
        setMessages(prev => [...prev, aiMsg]);
        await speakText(data.response);
      }
    } catch (err) {
      setError('Failed to get response');
    } finally {
      setIsProcessing(false);
    }
  };

  const speakText = async (text) => {
    setIsSpeaking(true);

    try {
      const response = await fetch('http://localhost:5000/api/tts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text, language: selectedLanguage })
      });

      const data = await response.json();

      if (data.success && data.audio) {
        const audio = new Audio(`data:audio/wav;base64,${data.audio}`);
        audioRef.current = audio;
        audio.onended = () => setIsSpeaking(false);
        audio.onerror = () => setIsSpeaking(false);
        await audio.play();
      }
    } catch (err) {
      setIsSpeaking(false);
    }
  };

  const handleQuickAction = (action) => {
    const queries = {
      'Weather': 'What is the weather forecast for farming this week?',
      'Market': 'What are the current market prices for wheat and rice?',
      'Crop': 'What crops should I plant this season?',
      'Pest': 'How can I control pests in my wheat crop?'
    };
    setInputText(queries[action.title]);
    setShowChat(true);
  };

  const currentLang = languages.find(l => l.code === selectedLanguage);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="w-12 h-12 bg-gradient-to-br from-green-400 to-emerald-500 rounded-full flex items-center justify-center">
              <Sprout className="w-7 h-7 text-white" />
            </div>
            <h1 className="text-2xl font-bold text-gray-800">KissanAI</h1>
          </div>

          <div className="flex items-center gap-4">
            <select
              value={selectedLanguage}
              onChange={(e) => setSelectedLanguage(e.target.value)}
              className="px-4 py-2 bg-gray-800 text-white rounded-lg text-sm font-medium focus:outline-none focus:ring-2 focus:ring-green-500"
            >
              {languages.map(lang => (
                <option key={lang.code} value={lang.code}>
                  üåê IN {lang.name}
                </option>
              ))}
            </select>

            <div className="flex items-center gap-2 text-sm">
              <Wifi className={`w-4 h-4 ${serverStatus === 'connected' ? 'text-green-500' : 'text-red-500'}`} />
              <span className={serverStatus === 'connected' ? 'text-green-600' : 'text-red-600'}>
                {serverStatus === 'connected' ? 'Connected' : 'Offline'}
              </span>
            </div>

            <button className="px-4 py-2 bg-white border border-gray-300 rounded-lg text-sm font-medium hover:bg-gray-50 flex items-center gap-2">
              <MessageCircle className="w-4 h-4" />
              Feedback
            </button>

            <button 
              onClick={() => setShowChat(!showChat)}
              className="p-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
            >
              <Mic className="w-5 h-5" />
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-6 py-12">
        {!showChat ? (
          <div className="space-y-12">
            {/* Title */}
            <div className="text-center">
              <div className="flex items-center justify-center gap-3 mb-4">
                <Sprout className="w-10 h-10 text-green-600" />
                <h2 className="text-4xl font-bold text-gray-800">KissanAI Agent</h2>
              </div>
            </div>

            {/* Quick Actions */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 max-w-5xl mx-auto">
              {quickActions.map((action, idx) => (
                <button
                  key={idx}
                  onClick={() => handleQuickAction(action)}
                  className="bg-white rounded-2xl p-8 shadow-md hover:shadow-xl transition-all border border-gray-100 group hover:scale-105"
                >
                  <div className={`w-20 h-20 mx-auto mb-4 bg-gradient-to-br ${action.color} rounded-full flex items-center justify-center group-hover:scale-110 transition-transform`}>
                    <action.icon className="w-10 h-10 text-white" />
                  </div>
                  <div className="text-center">
                    <p className="text-lg font-bold text-gray-800">{action.title}</p>
                    <p className="text-gray-600">{action.subtitle}</p>
                  </div>
                </button>
              ))}
            </div>

            {/* Input Section */}
            <div className="max-w-4xl mx-auto space-y-6">
              <div className="relative">
                <input
                  type="text"
                  value={inputText}
                  onChange={(e) => setInputText(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                  placeholder="Ask about weather, market prices, or farming advice..."
                  className="w-full px-6 py-4 pr-14 bg-white border border-gray-300 rounded-full text-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent shadow-sm"
                />
                <button
                  onClick={handleSendMessage}
                  disabled={!inputText.trim()}
                  className="absolute right-2 top-1/2 -translate-y-1/2 p-3 bg-green-500 text-white rounded-full hover:bg-green-600 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  <Send className="w-5 h-5" />
                </button>
              </div>

              {/* Voice Button */}
              <div className="text-center">
                <button
                  onClick={isListening ? stopListening : startListening}
                  disabled={isProcessing || isSpeaking}
                  className={`w-32 h-32 rounded-full flex items-center justify-center transition-all shadow-2xl ${
                    isListening
                      ? 'bg-red-500 hover:bg-red-600 animate-pulse scale-110'
                      : 'bg-green-500 hover:bg-green-600 hover:scale-105'
                  } ${(isProcessing || isSpeaking) ? 'opacity-50 cursor-not-allowed' : ''}`}
                >
                  <Mic className="w-16 h-16 text-white" />
                </button>

                {isListening && (
                  <p className="mt-4 text-green-600 font-semibold animate-pulse">
                    Listening... Speak now
                  </p>
                )}
                {isProcessing && (
                  <div className="mt-4 flex items-center justify-center gap-2 text-blue-600">
                    <Loader2 className="w-5 h-5 animate-spin" />
                    <p className="font-semibold">Processing...</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        ) : (
          /* Chat Interface */
          <div className="max-w-4xl mx-auto">
            <div className="bg-white rounded-2xl shadow-lg border border-gray-200 overflow-hidden">
              {/* Chat Header */}
              <div className="bg-gradient-to-r from-green-500 to-emerald-600 px-6 py-4 flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <MessageSquare className="w-6 h-6 text-white" />
                  <h3 className="text-xl font-bold text-white">Conversation</h3>
                </div>
                <button
                  onClick={() => setShowChat(false)}
                  className="text-white hover:bg-white/20 rounded-lg px-4 py-2 text-sm font-medium"
                >
                  Back to Home
                </button>
              </div>

              {/* Messages */}
              <div className="h-96 overflow-y-auto p-6 space-y-4 bg-gray-50">
                {messages.length === 0 ? (
                  <div className="text-center text-gray-500 mt-20">
                    <MessageSquare className="w-16 h-16 mx-auto mb-4 text-gray-300" />
                    <p>Start a conversation with KissanAI</p>
                  </div>
                ) : (
                  messages.map((msg, idx) => (
                    <div
                      key={idx}
                      className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}
                    >
                      <div
                        className={`max-w-lg px-5 py-3 rounded-2xl ${
                          msg.role === 'user'
                            ? 'bg-green-500 text-white'
                            : 'bg-white text-gray-800 border border-gray-200'
                        }`}
                      >
                        <p className="text-sm font-medium mb-1">
                          {msg.role === 'user' ? 'You' : 'KissanAI'}
                        </p>
                        <p className="leading-relaxed">{msg.content}</p>
                      </div>
                    </div>
                  ))
                )}
              </div>

              {/* Input */}
              <div className="border-t border-gray-200 p-4 bg-white">
                <div className="flex gap-3">
                  <input
                    type="text"
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    placeholder="Type your message..."
                    className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                  />
                  <button
                    onClick={isListening ? stopListening : startListening}
                    className={`p-3 rounded-lg ${
                      isListening ? 'bg-red-500' : 'bg-gray-200'
                    } hover:opacity-90`}
                  >
                    <Mic className={`w-6 h-6 ${isListening ? 'text-white' : 'text-gray-600'}`} />
                  </button>
                  <button
                    onClick={handleSendMessage}
                    disabled={!inputText.trim()}
                    className="px-6 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 disabled:opacity-50 disabled:cursor-not-allowed font-medium"
                  >
                    Send
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="border-t border-gray-200 bg-white mt-20">
        <div className="max-w-7xl mx-auto px-6 py-4 text-center">
          <p className="text-sm text-gray-600">
            <span className="font-medium text-green-600">Home</span> | ¬© 2025 KissanAI
          </p>
        </div>
      </footer>

      {/* Error Toast */}
      {error && (
        <div className="fixed bottom-6 right-6 bg-red-500 text-white px-6 py-3 rounded-lg shadow-lg">
          {error}
        </div>
      )}
    </div>
  );
};

export default KissanAI;
