import React, { useState, useRef } from 'react';
import { Mic, MicOff, Volume2, MessageSquare } from 'lucide-react';

export default function App() {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [text, setText] = useState('');
  const [messages, setMessages] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState('hi-IN');

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const audioRef = useRef(new Audio());

  const languages = [
    { code: 'hi-IN', name: 'à¤¹à¤¿à¤‚à¤¦à¥€', flag: 'ðŸ‡®ðŸ‡³' },
    { code: 'en-US', name: 'English', flag: 'ðŸ‡ºðŸ‡¸' },
  ];

  // Start recording
  const startListening = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream, { mimeType: 'audio/webm' });
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = e => {
        if (e.data.size > 0) audioChunksRef.current.push(e.data);
      };

      mediaRecorderRef.current.onstop = async () => {
        const blob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        await sendAudioToSTT(blob);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current.start();
      setIsListening(true);
    } catch (err) {
      console.error('Mic error:', err);
    }
  };

  // Stop recording
  const stopListening = () => {
    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
      setIsListening(false);
    }
  };

  // Send audio to backend for STT
  const sendAudioToSTT = async (audioBlob) => {
    setIsProcessing(true);
    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'audio.webm');
      formData.append('language', selectedLanguage);

      const res = await fetch('http://localhost:5000/api/stt', { method: 'POST', body: formData });
      const data = await res.json();
      setText(data.transcript); // Display the transcript in the input box
    } catch (err) {
      console.error('STT error:', err);
    } finally {
      setIsProcessing(false);
    }
  };

  // Send message to AI
  const sendMessage = async () => {
    if (!text.trim()) return;

    const userMsg = { role: 'user', content: text };
    setMessages(prev => [...prev, userMsg]);
    setText('');

    setIsProcessing(true);
    try {
      const res = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userMsg.content, language: selectedLanguage })
      });
      const data = await res.json();
      const aiMsg = { role: 'assistant', content: data.response };
      setMessages(prev => [...prev, aiMsg]);

      // Play TTS
      const ttsRes = await fetch('http://localhost:5000/api/tts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: data.response, language: selectedLanguage })
      });
      const ttsData = await ttsRes.json();
      const audioBlob = base64ToBlob(ttsData.audio, 'audio/mp3');
      const audioUrl = URL.createObjectURL(audioBlob);
      audioRef.current.src = audioUrl;
      audioRef.current.play();
      audioRef.current.onended = () => URL.revokeObjectURL(audioUrl);

    } catch (err) {
      console.error('Chat error:', err);
    } finally {
      setIsProcessing(false);
    }
  };

  const base64ToBlob = (b64, mime) => {
    const bytes = atob(b64);
    const arr = new Uint8Array(bytes.length);
    for (let i = 0; i < bytes.length; i++) arr[i] = bytes.charCodeAt(i);
    return new Blob([arr], { type: mime });
  };

  return (
    <div className="min-h-screen bg-green-50 p-6 flex flex-col items-center">
      <h1 className="text-3xl font-bold mb-6">KisanAI ðŸŒ¾</h1>

      <div className="flex items-center gap-4 mb-4">
        <button
          onClick={isListening ? stopListening : startListening}
          className={`w-16 h-16 rounded-full flex items-center justify-center text-white ${
            isListening ? 'bg-red-500 animate-pulse' : 'bg-green-500 hover:scale-105'
          }`}
        >
          {isListening ? <MicOff /> : <Mic />}
        </button>

        <select value={selectedLanguage} onChange={e => setSelectedLanguage(e.target.value)} className="px-4 py-2 rounded border">
          {languages.map(lang => <option key={lang.code} value={lang.code}>{lang.flag} {lang.name}</option>)}
        </select>
      </div>

      <textarea
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Your speech will appear here..."
        className="w-full max-w-xl p-4 border rounded-lg mb-4 text-lg"
        rows={3}
      />

      <button
        onClick={sendMessage}
        disabled={isProcessing || !text.trim()}
        className="bg-green-500 text-white px-6 py-3 rounded-lg hover:bg-green-600 mb-6"
      >
        Send
      </button>

      <div className="w-full max-w-xl flex flex-col gap-4">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`p-4 rounded-lg ${msg.role === 'user' ? 'bg-purple-200 self-end' : 'bg-green-200 self-start'}`}
          >
            {msg.content}
          </div>
        ))}
      </div>
    </div>
  );
}
