import React, { useState, useRef, useEffect } from 'react';
import { Mic, MicOff, Volume2, Loader2, MessageSquare, CheckCircle, XCircle, Sprout, AlertCircle } from 'lucide-react';

const FarmerChatbot = () => {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [editedTranscript, setEditedTranscript] = useState('');
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('hi-IN');
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState('');
  const [voiceAvailable, setVoiceAvailable] = useState(false);

  const recognitionRef = useRef(null);
  const synthRef = useRef(window.speechSynthesis);

  const languages = [
    { code: 'hi-IN', name: 'рд╣рд┐рдВрджреА (Hindi)', flag: 'ЁЯЗоЁЯЗ│', sample: 'рдирдорд╕реНрддреЗ, рдореИрдВ рдХрд┐рд╕рд╛рди рдорд┐рддреНрд░ рд╣реВрдВ' },
    { code: 'en-US', name: 'English', flag: 'ЁЯЗмЁЯЗз', sample: 'Hello, I am Farmer Assistant' },
    { code: 'pa-IN', name: 'рикрй░риЬри╛римрйА (Punjabi)', flag: 'ЁЯЗоЁЯЗ│', sample: 'ри╕рид ри╕рйНри░рйА риЕриХри╛ри▓' },
    { code: 'mr-IN', name: 'рдорд░рд╛рдареА (Marathi)', flag: 'ЁЯЗоЁЯЗ│', sample: 'рдирдорд╕реНрдХрд╛рд░' },
    { code: 'ta-IN', name: 'родрооро┐ро┤рпН (Tamil)', flag: 'ЁЯЗоЁЯЗ│', sample: 'ро╡рогроХрпНроХроорпН' },
    { code: 'te-IN', name: 'р░др▒Жр░▓р▒Бр░Чр▒Б (Telugu)', flag: 'ЁЯЗоЁЯЗ│', sample: 'р░ир░ор░╕р▒Нр░Хр░╛р░░р░В' },
    { code: 'bn-IN', name: 'ржмрж╛ржВрж▓рж╛ (Bengali)', flag: 'ЁЯЗоЁЯЗ│', sample: 'ржиржорж╕рзНржХрж╛рж░' },
    { code: 'gu-IN', name: 'ркЧрлБркЬрк░рк╛ркдрлА (Gujarati)', flag: 'ЁЯЗоЁЯЗ│', sample: 'ркиркорк╕рлНркдрлЗ' },
    { code: 'kn-IN', name: 'р▓Хр▓ир│Нр▓ир▓б (Kannada)', flag: 'ЁЯЗоЁЯЗ│', sample: 'р▓ир▓ор▓╕р│Нр▓Хр▓╛р▓░' },
    { code: 'ml-IN', name: 'р┤ор┤▓р┤пр┤╛р┤│р┤В (Malayalam)', flag: 'ЁЯЗоЁЯЗ│', sample: 'р┤ир┤ор┤╕р╡Нр┤Хр┤╛р┤░р┤В' },
  ];

  useEffect(() => {
    // Check browser support
    const isChrome = /Chrome/.test(navigator.userAgent) && /Google Inc/.test(navigator.vendor);

    if (!isChrome) {
      setError('тЪая╕П рд╕рд░реНрд╡реЛрддреНрддрдо рдкрд░рд┐рдгрд╛рдо рдХреЗ рд▓рд┐рдП рдХреГрдкрдпрд╛ Google Chrome рдХрд╛ рдЙрдкрдпреЛрдЧ рдХрд░реЗрдВ');
    }

    // Initialize Speech Recognition
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;

      recognitionRef.current.onresult = (event) => {
        const speechResult = event.results[0][0].transcript;
        const confidence = event.results[0][0].confidence;

        console.log('Recognized:', speechResult, 'Confidence:', confidence);

        setTranscript(speechResult);
        setEditedTranscript(speechResult);
        setIsListening(false);
        setShowConfirmation(true);
      };

      recognitionRef.current.onerror = (event) => {
        console.error('Speech error:', event.error);
        if (event.error === 'no-speech') {
          setError('рдХреЛрдИ рдЖрд╡рд╛рдЬ рдирд╣реАрдВ рд╕реБрдирд╛рдИ рджреАред рдХреГрдкрдпрд╛ рдлрд┐рд░ рд╕реЗ рдкреНрд░рдпрд╛рд╕ рдХрд░реЗрдВред');
        } else if (event.error === 'audio-capture') {
          setError('рдорд╛рдЗрдХреНрд░реЛрдлреЛрди рдПрдХреНрд╕реЗрд╕ рдирд╣реАрдВ рдорд┐рд▓рд╛ред рдХреГрдкрдпрд╛ рдЕрдиреБрдорддрд┐ рджреЗрдВред');
        } else {
          setError(`рд╡рд╛рдгреА рдкрд╣рдЪрд╛рди рддреНрд░реБрдЯрд┐: ${event.error}`);
        }
        setIsListening(false);
      };

      recognitionRef.current.onend = () => {
        setIsListening(false);
      };
    } else {
      setError('тЭМ рд╡рд╛рдгреА рдкрд╣рдЪрд╛рди рд╕рдорд░реНрдерд┐рдд рдирд╣реАрдВ рд╣реИред рдХреГрдкрдпрд╛ Google Chrome рдХрд╛ рдЙрдкрдпреЛрдЧ рдХрд░реЗрдВред');
    }

    // Check TTS voices availability
    const loadVoices = () => {
      const voices = synthRef.current.getVoices();
      const hasIndianVoices = voices.some(v => v.lang.startsWith('hi') || v.lang.startsWith('en-IN'));
      setVoiceAvailable(voices.length > 0);
    };

    loadVoices();
    if (synthRef.current.onvoiceschanged !== undefined) {
      synthRef.current.onvoiceschanged = loadVoices;
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      synthRef.current.cancel();
    };
  }, []);

  const startListening = () => {
    setError('');
    setTranscript('');
    setEditedTranscript('');
    setShowConfirmation(false);

    if (recognitionRef.current) {
      try {
        recognitionRef.current.lang = selectedLanguage;
        recognitionRef.current.start();
        setIsListening(true);
      } catch (err) {
        setError('рд╕реБрдирдирд╛ рд╢реБрд░реВ рдХрд░рдиреЗ рдореЗрдВ рд╡рд┐рдлрд▓: ' + err.message);
      }
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

  const handleConfirm = async () => {
    setShowConfirmation(false);

    const userMsg = { role: 'user', content: editedTranscript, language: selectedLanguage };
    setMessages(prev => [...prev, userMsg]);

    await getAIResponse(editedTranscript, selectedLanguage);
  };

  const handleCancel = () => {
    setShowConfirmation(false);
    setTranscript('');
    setEditedTranscript('');
  };

  const getAIResponse = async (userInput, language) => {
    setIsProcessing(true);

    try {
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: userInput,
          language: language
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      const aiResponse = data.response;

      const aiMsg = { role: 'assistant', content: aiResponse, language };
      setMessages(prev => [...prev, aiMsg]);

      await speakText(aiResponse, language);

    } catch (err) {
      console.error('Error:', err);
      setError('AI рдЙрддреНрддрд░ рдкреНрд░рд╛рдкреНрдд рдХрд░рдиреЗ рдореЗрдВ рд╡рд┐рдлрд▓: ' + err.message);
    } finally {
