import React, { useState, useRef } from 'react';
import { Mic, MicOff } from 'lucide-react';

export default function App() {
  const [isListening, setIsListening] = useState(false);
  const [text, setText] = useState('');
  const [messages, setMessages] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState('hi-IN');
  const [isProcessing, setIsProcessing] = useState(false);

  const audioRef = useRef(new Audio());
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const languages = [
    { code: 'hi-IN', name: 'à¤¹à¤¿à¤‚à¤¦à¥€ (Hindi)' },
    { code: 'en-US', name: 'English' },
    { code: 'mr-IN', name: 'à¤®à¤°à¤¾à¤ à¥€ (Marathi)' },
    { code: 'gu-IN', name: 'àª—à«àªœàª°àª¾àª¤à«€ (Gujarati)' },
    { code: 'bn-IN', name: 'à¦¬à¦¾à¦‚à¦²à¦¾ (Bengali)' },
    { code: 'te-IN', name: 'à°¤à±†à°²à±à°—à± (Telugu)' },
    { code: 'ta-IN', name: 'à®¤à®®à®¿à®´à¯ (Tamil)' },
    { code: 'kn-IN', name: 'à²•à²¨à³à²¨à²¡ (Kannada)' },
    { code: 'ml-IN', name: 'à´®à´²à´¯à´¾à´³à´‚ (Malayalam)' },
    { code: 'pa-IN', name: 'à¨ªà©°à¨œà¨¾à¨¬à©€ (Punjabi)' }
  ];

  // ðŸŽ¤ Start listening
  const startListening = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm;codecs=opus'
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.start();
      setIsListening(true);
    } catch (err) {
      alert('Microphone permission denied');
      console.error(err);
    }
  };

  // ðŸ›‘ Stop listening
  const stopListening = () => {
    const recorder = mediaRecorderRef.current;
    if (!recorder) return;

    recorder.stop();
    setIsListening(false);

    recorder.onstop = async () => {
      const audioBlob = new Blob(audioChunksRef.current, {
        type: 'audio/webm'
      });

      const base64Audio = await blobToBase64(audioBlob);
      await sendAudioToBackend(base64Audio);
    };
  };

  // ðŸ” Send audio â†’ backend STT
  const sendAudioToBackend = async (audioBase64) => {
    try {
      setIsProcessing(true);

      const res = await fetch('http://localhost:8000/voice/stt', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          audio: audioBase64,
          language: selectedLanguage,
          farmer_id: 'farmer_123',
          farmer_name: 'Ramesh Kumar'
        })
      });

      const data = await res.json();

      if (!data.text) return;

      // Show user text
      setMessages((prev) => [...prev, { role: 'user', content: data.text }]);

      // Show AI text
      setMessages((prev) => [...prev, { role: 'assistant', content: data.text }]);

      // Play TTS
      const audioBlob = base64ToBlob(data.audio, 'audio/mp3');
      const audioUrl = URL.createObjectURL(audioBlob);
      audioRef.current.src = audioUrl;
      audioRef.current.play();
    } catch (err) {
      console.error('STT failed:', err);
    } finally {
      setIsProcessing(false);
    }
  };

  // ðŸ”„ Helpers
  const blobToBase64 = (blob) => {
    return new Promise((resolve) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result.split(',')[1]);
      reader.readAsDataURL(blob);
    });
  };

  const base64ToBlob = (b64, mime) => {
    const bytes = atob(b64);
    const arr = new Uint8Array(bytes.length);
    for (let i = 0; i < bytes.length; i++) arr[i] = bytes.charCodeAt(i);
    return new Blob([arr], { type: mime });
  };

  return (
    <div className="min-h-screen bg-green-50 p-6 flex flex-col items-center">
      <h1 className="text-3xl font-bold mb-6">KisanAI ðŸŒ¾</h1>

      <div className="flex items-center gap-4 mb-4">
        <button
          onClick={isListening ? stopListening : startListening}
          className={`w-16 h-16 rounded-full flex items-center justify-center text-white ${
            isListening ? 'bg-red-500 animate-pulse' : 'bg-green-500'
          }`}
        >
          {isListening ? <MicOff /> : <Mic />}
        </button>

        <select
          value={selectedLanguage}
          onChange={(e) => setSelectedLanguage(e.target.value)}
          className="px-4 py-2 rounded border"
        >
          {languages.map((lang) => (
            <option key={lang.code} value={lang.code}>
              {lang.name}
            </option>
          ))}
        </select>
      </div>

      <div className="w-full max-w-xl flex flex-col gap-4">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`p-4 rounded-lg ${
              msg.role === 'user'
                ? 'bg-purple-200 self-end'
                : 'bg-green-200 self-start'
            }`}
          >
            {msg.content}
          </div>
        ))}
      </div>

      {isProcessing && (
        <p className="mt-4 text-gray-600">Processing voiceâ€¦</p>
      )}
    </div>
  );
}
