import React, { useState, useRef, useEffect } from 'react';
import { Mic, MicOff, Volume2, Loader2, MessageSquare, Sun, Tractor } from 'lucide-react';

export default function App() {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [editedTranscript, setEditedTranscript] = useState('');
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('hi-IN');
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState('');
  const [weather, setWeather] = useState(null);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const audioRef = useRef(new Audio());

  const languages = [
    { code: 'hi-IN', name: '‡§π‡§ø‡§Ç‡§¶‡•Ä', flag: 'üáÆüá≥' },
    { code: 'en-US', name: 'English', flag: 'üá∫üá∏' },
    { code: 'bn-IN', name: '‡¶¨‡¶æ‡¶Ç‡¶≤‡¶æ', flag: 'üáÆüá≥' },
    { code: 'te-IN', name: '‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å', flag: 'üáÆüá≥' },
    { code: 'ta-IN', name: '‡Æ§‡ÆÆ‡Æø‡Æ¥‡Øç', flag: 'üáÆüá≥' },
    { code: 'mr-IN', name: '‡§Æ‡§∞‡§æ‡§†‡•Ä', flag: 'üáÆüá≥' },
    { code: 'gu-IN', name: '‡™ó‡´Å‡™ú‡™∞‡™æ‡™§‡´Ä', flag: 'üáÆüá≥' },
    { code: 'kn-IN', name: '‡≤ï‡≤®‡≥ç‡≤®‡≤°', flag: 'üáÆüá≥' },
    { code: 'ml-IN', name: '‡¥Æ‡¥≤‡¥Ø‡¥æ‡¥≥‡¥Ç', flag: 'üáÆüá≥' },
    { code: 'pa-IN', name: '‡®™‡©∞‡®ú‡®æ‡®¨‡©Ä', flag: 'üáÆüá≥' }
  ];

  useEffect(() => {
    fetchWeather();
  }, []);

  const fetchWeather = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/weather');
      const data = await res.json();
      setWeather(data);
    } catch {}
  };

  const startListening = async () => {
    setError('');
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream, { mimeType: 'audio/webm' });
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = e => {
        if (e.data.size > 0) audioChunksRef.current.push(e.data);
      };

      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        await sendAudioToSTT(audioBlob);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current.start();
      setIsListening(true);
    } catch (err) {
      setError('Microphone access denied: ' + err.message);
    }
  };

  const stopListening = () => {
    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
      setIsListening(false);
    }
  };

  const sendAudioToSTT = async (audioBlob) => {
    setIsProcessing(true);
    setError('');
    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'audio.webm');
      formData.append('language', selectedLanguage);

      const response = await fetch('http://localhost:5000/api/stt', { method: 'POST', body: formData });
      const data = await response.json();
      setTranscript(data.transcript);
      setEditedTranscript(data.transcript);
      setShowConfirmation(true);
    } catch (err) {
      setError('Speech recognition failed: ' + err.message);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleConfirm = async () => {
    setShowConfirmation(false);
    const userMsg = { role: 'user', content: editedTranscript, language: selectedLanguage };
    setMessages(prev => [...prev, userMsg]);
    await getKisanAIResponse(editedTranscript, selectedLanguage);
  };

  const getKisanAIResponse = async (text, language) => {
    setIsProcessing(true);
    try {
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: text, language })
      });
      const data = await response.json();
      const aiMsg = { role: 'assistant', content: data.response, language, model: data.model };
      setMessages(prev => [...prev, aiMsg]);
      await speakText(data.response, language);
    } catch (err) {
      setError('KisanAI response failed: ' + err.message);
    } finally {
      setIsProcessing(false);
    }
  };

  const speakText = async (text, language) => {
    try {
      setIsSpeaking(true);
      const response = await fetch('http://localhost:5000/api/tts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text, language })
      });
      const data = await response.json();
      const audioBlob = base64ToBlob(data.audio, 'audio/mp3');
      const audioUrl = URL.createObjectURL(audioBlob);
      audioRef.current.src = audioUrl;
      audioRef.current.onended = () => {
        setIsSpeaking(false);
        URL.revokeObjectURL(audioUrl);
      };
      await audioRef.current.play();
    } catch (err) {
      setIsSpeaking(false);
    }
  };

  const base64ToBlob = (b64, mime) => {
    const byteChars = atob(b64);
    const byteNumbers = new Array(byteChars.length).fill().map((_, i) => byteChars.charCodeAt(i));
    return new Blob([new Uint8Array(byteNumbers)], { type: mime });
  };

  const stopSpeaking = () => {
    audioRef.current.pause();
    audioRef.current.currentTime = 0;
    setIsSpeaking(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 via-yellow-50 to-orange-50 p-4">
      <div className="max-w-2xl mx-auto">
        {/* Header */}
        <div className="bg-gradient-to-r from-green-600 to-emerald-600 text-white rounded-2xl p-8 mb-6 shadow-2xl flex flex-col gap-4">
          <div className="flex items-center gap-4">
            <Tractor className="w-12 h-12" />
            <div>
              <h1 className="text-3xl font-bold">KisanAI</h1>
              <p className="text-green-100">üåæ Voice-Powered Agricultural Assistant</p>
            </div>
          </div>
          {weather && (
            <div className="bg-white/20 backdrop-blur-sm rounded-xl p-4 flex items-center gap-3">
              <Sun className="w-6 h-6 text-yellow-300" />
              <div>
                <p className="font-semibold">{weather.city}: {weather.temp}</p>
                <p className="text-sm">{weather.condition}</p>
                <p className="text-xs">{weather.farmingTip}</p>
              </div>
            </div>
          )}
        </div>

        {/* Voice Controls */}
        <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-8 shadow-xl mb-6 flex flex-col items-center gap-6">
          <button
            onClick={isListening ? stopListening : startListening}
            disabled={isProcessing || isSpeaking || showConfirmation}
            className={`w-28 h-28 rounded-full flex items-center justify-center transition-all transform ${
              isListening
                ? 'bg-red-500 shadow-red-500/50 animate-pulse scale-110'
                : 'bg-gradient-to-r from-green-500 to-emerald-500 shadow-green-500/50 hover:scale-105'
            } shadow-2xl disabled:opacity-50`}
          >
            {isListening ? <MicOff className="w-12 h-12 text-white" /> : <Mic className="w-12 h-12 text-white" />}
          </button>

          <div className="text-center space-y-2">
            {isListening && <p className="text-xl font-bold text-red-600 animate-pulse">üé§ Recording...</p>}
            {isProcessing && (
              <div className="flex items-center gap-2 text-emerald-600">
                <Loader2 className="w-6 h-6 animate-spin" />
                <p className="text-lg font-bold">Processing...</p>
              </div>
            )}
            {isSpeaking && (
              <div className="flex flex-col items-center gap-2">
                <div className="flex items-center gap-2 text-blue-600">
                  <Volume2 className="w-6 h-6 animate-pulse" />
                  <p className="text-lg font-bold">Speaking...</p>
                </div>
                <button onClick={stopSpeaking} className="text-sm bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600">
                  Stop Audio
                </button>
              </div>
            )}
          </div>

          <select
            value={selectedLanguage}
            onChange={(e) => setSelectedLanguage(e.target.value)}
            className="w-full px-6 py-3 bg-white border-2 border-green-200 rounded-xl text-lg font-semibold focus:border-green-500"
            disabled={isListening || isProcessing}
          >
            {languages.map(lang => <option key={lang.code} value={lang.code}>{lang.flag} {lang.name}</option>)}
          </select>
        </div>

        {/* Confirmation */}
        {showConfirmation && (
          <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl p-8 border-4 border-indigo-200 shadow-2xl mb-6">
            <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">‚úèÔ∏è You said:</h3>
            <textarea
              value={editedTranscript}
              onChange={(e) => setEditedTranscript(e.target.value)}
              className="w-full p-6 border-2 border-gray-300 rounded-xl focus:border-green-500 focus:outline-none text-lg font-medium mb-6 min-h-[100px]"
            />
            <div className="flex gap-4">
              <button onClick={handleConfirm} className="flex-1 bg-green-500 hover:bg-green-600 text-white font-bold py-4 px-8 rounded-xl shadow-lg">‚úÖ Send</button>
              <button onClick={() => setShowConfirmation(false)} className="flex-1 bg-gray-500 hover:bg-gray-600 text-white font-bold py-4 px-8 rounded-xl shadow-lg">‚ùå Cancel</button>
            </div>
          </div>
        )}

        {/* Chat History */}
        {messages.length > 0 && (
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-xl max-h-96 overflow-y-auto">
            <h2 className="text-2xl font-bold text-gray-800 mb-4 flex items-center gap-2">
              <MessageSquare className="w-7 h-7 text-green-600" /> Conversation
            </h2>
            <div className="space-y-4">
              {messages.map((msg, idx) => (
                <div key={idx} className={`p-6 rounded-xl shadow-md ${
                  msg.role === 'user'
                    ? 'bg-gradient-to-r from-purple-500 to-indigo-500 text-white'
                    : 'bg-gradient-to-r from-green-100 to-emerald-100'
                }`}>
                  <div className="flex items-center justify-between mb-3">
                    <span className="font-bold text-lg">{msg.role === 'user' ? 'üë®‚Äçüåæ You' : 'ü§ñ KisanAI'}</span>
                    <span className="text-sm bg-white/50 px-3 py-1 rounded-full">{msg.language}</span>
                  </div>
                  <p className="text-lg leading-relaxed">{msg.content}</p>
                  {msg.model && <p className="text-xs mt-2 opacity-75">Model: {msg.model}</p>}
                </div>
              ))}
            </div>
          </div>
        )}

        {error && (
          <div className="bg-red-50 border-2 border-red-200 rounded-xl p-6 mt-4">
            <p className="text-red-800 font-semibold">{error}</p>
          </div>
        )}
      </div>
    </div>
  );
}
