import React, { useState, useRef, useEffect } from 'react';
import { Mic, MicOff, Volume2, Loader2, MessageSquare, Languages } from 'lucide-react';

const App = () => {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [detectedLanguage, setDetectedLanguage] = useState('');
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState('');

  const recognitionRef = useRef(null);
  const synthRef = useRef(window.speechSynthesis);

  // Initialize Speech Recognition
  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;

      recognitionRef.current.onresult = async (event) => {
        const speechResult = event.results[0][0].transcript;
        const lang = event.results[0][0].lang || 'en-US';

        setTranscript(speechResult);
        setDetectedLanguage(lang);
        setIsListening(false);

        const userMsg = { role: 'user', content: speechResult, language: lang };
        setMessages(prev => [...prev, userMsg]);

        await getAIResponse(speechResult, lang);
      };

      recognitionRef.current.onerror = (event) => {
        setError(`Speech recognition error: ${event.error}`);
        setIsListening(false);
      };

      recognitionRef.current.onend = () => {
        setIsListening(false);
      };
    } else {
      setError('Speech recognition not supported in this browser');
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      synthRef.current.cancel();
    };
  }, []);

  const startListening = () => {
    setError('');
    setTranscript('');

    if (recognitionRef.current) {
      try {
        recognitionRef.current.lang = '';
        recognitionRef.current.start();
        setIsListening(true);
      } catch (err) {
        setError('Failed to start listening: ' + err.message);
      }
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

const getAIResponse = async (userInput, language) => {
  setIsProcessing(true);

  try {
    // Call your backend instead of Anthropic API directly
    const response = await fetch('http://localhost:5000/api/chat', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        message: userInput,
        language: language
      })
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    const aiResponse = data.response;

    const aiMsg = { role: 'assistant', content: aiResponse, language };
    setMessages(prev => [...prev, aiMsg]);

    await speakText(aiResponse, language);

  } catch (err) {
    console.error('Error:', err);
    setError('Failed to get AI response: ' + err.message);
  } finally {
    setIsProcessing(false);
  }
};
  const speakText = async (text, language) => {
    return new Promise((resolve) => {
      synthRef.current.cancel();

      const utterance = new SpeechSynthesisUtterance(text);

      const langMap = {
        'en-US': 'en-US',
        'es-ES': 'es-ES',
        'fr-FR': 'fr-FR',
        'de-DE': 'de-DE',
        'it-IT': 'it-IT',
        'pt-BR': 'pt-BR',
        'hi-IN': 'hi-IN',
        'zh-CN': 'zh-CN',
        'ja-JP': 'ja-JP',
        'ko-KR': 'ko-KR',
        'ar-SA': 'ar-SA',
        'ru-RU': 'ru-RU'
      };

      utterance.lang = langMap[language] || language || 'en-US';
      utterance.rate = 0.9;
      utterance.pitch = 1;

      const voices = synthRef.current.getVoices();
      const matchingVoice = voices.find(voice => voice.lang.startsWith(utterance.lang.split('-')[0]));
      if (matchingVoice) {
        utterance.voice = matchingVoice;
      }

      utterance.onstart = () => setIsSpeaking(true);
      utterance.onend = () => {
        setIsSpeaking(false);
        resolve();
      };
      utterance.onerror = () => {
        setIsSpeaking(false);
        resolve();
      };

      synthRef.current.speak(utterance);
    });
  };

  const stopSpeaking = () => {
    synthRef.current.cancel();
    setIsSpeaking(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50 p-4">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-lg p-6 mb-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
