import React, { useState, useRef, useEffect } from 'react';
import { Mic, MicOff, Volume2, Loader2, MessageSquare, Sun, Tractor } from 'lucide-react';

const App = () => {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [editedTranscript, setEditedTranscript] = useState('');
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('hi-IN');
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState('');
  const [weather, setWeather] = useState(null);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const audioRef = useRef(new Audio());

  const languages = [
    { code: 'hi-IN', name: '‡§π‡§ø‡§Ç‡§¶‡•Ä (Hindi)', flag: 'üáÆüá≥' },
    { code: 'en-US', name: 'English', flag: 'üá∫üá∏' },
    { code: 'bn-IN', name: '‡¶¨‡¶æ‡¶Ç‡¶≤‡¶æ (Bengali)', flag: 'üáÆüá≥' },
    { code: 'te-IN', name: '‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å (Telugu)', flag: 'üáÆüá≥' },
    { code: 'ta-IN', name: '‡Æ§‡ÆÆ‡Æø‡Æ¥‡Øç (Tamil)', flag: 'üáÆüá≥' },
    { code: 'mr-IN', name: '‡§Æ‡§∞‡§æ‡§†‡•Ä (Marathi)', flag: 'üáÆüá≥' },
    { code: 'gu-IN', name: '‡™ó‡´Å‡™ú‡™∞‡™æ‡™§‡´Ä (Gujarati)', flag: 'üáÆüá≥' },
    { code: 'kn-IN', name: '‡≤ï‡≤®‡≥ç‡≤®‡≤° (Kannada)', flag: 'üáÆüá≥' },
    { code: 'ml-IN', name: '‡¥Æ‡¥≤‡¥Ø‡¥æ‡¥≥‡¥Ç (Malayalam)', flag: 'üáÆüá≥' },
    { code: 'pa-IN', name: '‡®™‡©∞‡®ú‡®æ‡®¨‡©Ä (Punjabi)', flag: 'üáÆüá≥' }
  ];


  // Debug all fetch errors globally
window.addEventListener('unhandledrejection', (event) => {
  console.error('üö® Unhandled promise rejection:', event.reason);
});

  // Load Weather
  useEffect(() => {
    fetchWeather();
  }, []);

  const fetchWeather = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/weather');
      const data = await res.json();
      setWeather(data);
    } catch {}
  };

  // Start Recording Audio
  const startListening = async () => {
    setError('');
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

      mediaRecorderRef.current = new MediaRecorder(stream, {
        mimeType: 'audio/webm'
      });

      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        await sendAudioToSTT(audioBlob);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current.start();
      setIsListening(true);

      console.log('üé§ Recording started...');
    } catch (err) {
      setError('Microphone access denied: ' + err.message);
      console.error('Microphone error:', err);
    }
  };

  // Stop Recording
  const stopListening = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
      setIsListening(false);
      console.log('üõë Recording stopped');
    }
  };

  // Send Audio to Backend STT (OpenAI Whisper)
  // Send Audio to Backend STT (OpenAI Whisper) - ENHANCED
const sendAudioToSTT = async (audioBlob) => {
  setIsProcessing(true);
  setError('');  // Clear previous errors
  try {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'audio.webm');
    formData.append('language', selectedLanguage);

    console.log('üì§ Sending audio:', {
      size: audioBlob.size,
      type: audioBlob.type,
      language: selectedLanguage
    });

    const response = await fetch('http://localhost:5000/api/stt', {
      method: 'POST',
      body: formData
    });

    console.log('üì• Response status:', response.status);
    console.log('üì• Content-Type:', response.headers.get('content-type'));

    // Check if response is HTML error page
    const contentType = response.headers.get('content-type');
    if (contentType && contentType.includes('text/html')) {
      const htmlError = await response.text();
      console.error('üö® HTML Error Page:', htmlError.substring(0, 200));
      throw new Error('Backend returned HTML (404?). Check server console.');
    }

    const data = await response.json();

    if (!response.ok) {
      throw new Error(data.error || `HTTP ${response.status}`);
    }

    console.log('‚úÖ STT Success:', data.transcript);
    setTranscript(data.transcript);
    setEditedTranscript(data.transcript);
    setShowConfirmation(true);

  } catch (err) {
    console.error('‚ùå STT Full Error:', err);
    setError(`Speech failed: ${err.message}`);
  } finally {
    setIsProcessing(false);
  }
};


  // Confirm and Send to Chat
  const handleConfirm = async () => {
    setShowConfirmation(false);
    const userMsg = { role: 'user', content: editedTranscript, language: selectedLanguage };
    setMessages(prev => [...prev, userMsg]);

    await getKisanAIResponse(editedTranscript, selectedLanguage);
  };

  // Get AI Response
  const getKisanAIResponse = async (userInput, language) => {
    setIsProcessing(true);
    try {
      console.log('üí¨ Sending to chat API...');

      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userInput, language })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Chat failed');
      }

      const aiResponse = data.response;
      console.log('ü§ñ AI Response:', aiResponse);

      const aiMsg = { role: 'assistant', content: aiResponse, language, model: data.model };
      setMessages(prev => [...prev, aiMsg]);

      // Convert text to speech (Google TTS)
      await speakText(aiResponse, language);

    } catch (err) {
      setError('KisanAI response failed: ' + err.message);
      console.error('Chat Error:', err);
    } finally {
      setIsProcessing(false);
    }
  };

  // Text-to-Speech using Google Cloud TTS
  const speakText = async (text, language) => {
    try {
      console.log('üîä Requesting TTS...');
      setIsSpeaking(true);

      const response = await fetch('http://localhost:5000/api/tts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text, language })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'TTS failed');
      }

      // Convert base64 audio to blob and play
      const audioBlob = base64ToBlob(data.audio, 'audio/mp3');
      const audioUrl = URL.createObjectURL(audioBlob);

      audioRef.current.src = audioUrl;

      audioRef.current.onended = () => {
        setIsSpeaking(false);
        URL.revokeObjectURL(audioUrl);
      };

      await audioRef.current.play();
      console.log('‚ñ∂Ô∏è Playing audio...');

    } catch (err) {
      console.error('TTS Error:', err);
      setIsSpeaking(false);
    }
  };

  // Convert base64 to Blob
  const base64ToBlob = (base64, mimeType) => {
    const byteCharacters = atob(base64);
    const byteNumbers = new Array(byteCharacters.length);
    for (let i = 0; i < byteCharacters.length; i++) {
      byteNumbers[i] = byteCharacters.charCodeAt(i);
    }
    const byteArray = new Uint8Array(byteNumbers);
    return new Blob([byteArray], { type: mimeType });
  };

  // Stop Speaking
  const stopSpeaking = () => {
    audioRef.current.pause();
    audioRef.current.currentTime = 0;
    setIsSpeaking(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 via-yellow-50 to-orange-50 p-4">
      <div className="max-w-2xl mx-auto">
        {/* Header */}
        <div className="bg-gradient-to-r from-green-600 to-emerald-600 text-white rounded-2xl p-8 mb-6 shadow-2xl">
          <div className="flex items-center gap-4 mb-4">
            <Tractor className="w-12 h-12" />
            <div>
              <h1 className="text-3xl font-bold">KisanAI</h1>
              <p className="text-green-100">üåæ Voice-Powered Agricultural Assistant</p>
              <p className="text-xs text-green-200">OpenAI Whisper STT + Google Cloud TTS</p>
            </div>
          </div>

          {/* Weather Widget */}
          {weather && (
            <div className="bg-white/20 backdrop-blur-sm rounded-xl p-4">
              <div className="flex items-center gap-3">
                <Sun className="w-6 h-6 text-yellow-300" />
                <div>
                  <p className="font-semibold">{weather.city}: {weather.temp}</p>
                  <p className="text-sm">{weather.condition}</p>
                  <p className="text-xs">{weather.farmingTip}</p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Voice Controls */}
        <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-8 shadow-xl mb-6">
          <div className="flex flex-col items-center gap-6">
            <button
              onClick={isListening ? stopListening : startListening}
              disabled={isProcessing || isSpeaking || showConfirmation}
              className={`w-28 h-28 rounded-full flex items-center justify-center transition-all transform ${
                isListening
                  ? 'bg-red-500 shadow-red-500/50 animate-pulse scale-110'
                  : 'bg-gradient-to-r from-green-500 to-emerald-500 shadow-green-500/50 hover:scale-105'
              } shadow-2xl disabled:opacity-50`}
            >
              {isListening ? (
                <MicOff className="w-12 h-12 text-white" />
              ) : (
                <Mic className="w-12 h-12 text-white" />
              )}
            </button>

            <div className="text-center space-y-2">
              {isListening && (
                <p className="text-xl font-bold text-red-600 animate-pulse">üé§ Recording... (Click to stop)</p>
              )}
              {isProcessing && (
                <div className="flex items-center gap-2 text-emerald-600">
                  <Loader2 className="w-6 h-6 animate-spin" />
                  <p className="text-lg font-bold">Processing...</p>
                </div>
              )}
              {isSpeaking && (
                <div className="flex flex-col items-center gap-2">
                  <div className="flex items-center gap-2 text-blue-600">
                    <Volume2 className="w-6 h-6 animate-pulse" />
                    <p className="text-lg font-bold">Speaking...</p>
                  </div>
                  <button
                    onClick={stopSpeaking}
                    className="text-sm bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600"
                  >
                    Stop Audio
                  </button>
                </div>
              )}
            </div>

            {/* Language Selector */}
            <select
              value={selectedLanguage}
              onChange={(e) => setSelectedLanguage(e.target.value)}
              className="w-full px-6 py-3 bg-white border-2 border-green-200 rounded-xl text-lg font-semibold focus:border-green-500"
              disabled={isListening || isProcessing}
            >
              {languages.map(lang => (
                <option key={lang.code} value={lang.code}>
                  {lang.flag} {lang.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Confirmation Dialog */}
        {showConfirmation && (
          <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl p-8 border-4 border-indigo-200 shadow-2xl mb-6">
            <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
              ‚úèÔ∏è You said:
            </h3>
            <textarea
              value={editedTranscript}
              onChange={(e) => setEditedTranscript(e.target.value)}
              className="w-full p-6 border-2 border-gray-300 rounded-xl focus:border-green-500 focus:outline-none text-lg font-medium mb-6 min-h-[100px]"
              placeholder="Edit here..."
            />
            <div className="flex gap-4">
              <button
                onClick={handleConfirm}
                className="flex-1 bg-green-500 hover:bg-green-600 text-white font-bold py-4 px-8 rounded-xl shadow-lg transition-all"
              >
                ‚úÖ Send to KisanAI
              </button>
              <button
                onClick={() => setShowConfirmation(false)}
                className="flex-1 bg-gray-500 hover:bg-gray-600 text-white font-bold py-4 px-8 rounded-xl shadow-lg transition-all"
