import React, { useState, useRef } from 'react';

export default function App() {
  const [listening, setListening] = useState(false);
  const [text, setText] = useState('');
  const mediaRecorder = useRef(null);
  const audioChunks = useRef([]);

  const start = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    mediaRecorder.current = new MediaRecorder(stream, {
      mimeType: 'audio/webm'
    });

    audioChunks.current = [];

    mediaRecorder.current.ondataavailable = e => {
      audioChunks.current.push(e.data);
    };

    mediaRecorder.current.onstop = async () => {
      const blob = new Blob(audioChunks.current, { type: 'audio/webm' });
      sendAudio(blob);
    };

    mediaRecorder.current.start();
    setListening(true);
  };

  const stop = () => {
    mediaRecorder.current.stop();
    setListening(false);
  };

  const sendAudio = async blob => {
    const formData = new FormData();
    formData.append('audio', blob);
    formData.append('language', 'hi-IN');

    const stt = await fetch('http://localhost:5000/api/stt', {
      method: 'POST',
      body: formData
    });

    const sttData = await stt.json();
    setText(sttData.transcript);

    const chat = await fetch('http://localhost:5000/api/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        message: sttData.transcript,
        language: 'hi-IN'
      })
    });

    const chatData = await chat.json();

    const tts = await fetch('http://localhost:5000/api/tts', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        text: chatData.response,
        language: 'hi-IN'
      })
    });

    const ttsData = await tts.json();
    const audio = new Audio(`data:audio/mp3;base64,${ttsData.audio}`);
    audio.play();
  };

  return (
    <div style={{ padding: 40 }}>
      <h1>KisanAI ðŸŒ¾</h1>
      <button onClick={listening ? stop : start}>
        {listening ? 'Stop' : 'Speak'}
      </button>
      <p>{text}</p>
    </div>
  );
}
