import React, { useState, useRef, useEffect } from 'react';
import { Mic, MicOff, Volume2, Loader2, MessageSquare, Languages, AlertCircle } from 'lucide-react';

const App = () => {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('auto');
  const [detectedLanguage, setDetectedLanguage] = useState('');
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState('');

  const recognitionRef = useRef(null);
  const synthRef = useRef(window.speechSynthesis);

  const languages = [
    { code: 'auto', name: 'Auto-Detect', flag: 'üåç' },
    { code: 'en-US', name: 'English', flag: 'üá∫üá∏' },
    { code: 'es-ES', name: 'Spanish', flag: 'üá™üá∏' },
    { code: 'fr-FR', name: 'French', flag: 'üá´üá∑' },
    { code: 'de-DE', name: 'German', flag: 'üá©üá™' },
    { code: 'it-IT', name: 'Italian', flag: 'üáÆüáπ' },
    { code: 'pt-BR', name: 'Portuguese', flag: 'üáßüá∑' },
    { code: 'hi-IN', name: 'Hindi', flag: 'üáÆüá≥' },
    { code: 'zh-CN', name: 'Chinese', flag: 'üá®üá≥' },
    { code: 'ja-JP', name: 'Japanese', flag: 'üáØüáµ' },
    { code: 'ko-KR', name: 'Korean', flag: 'üá∞üá∑' },
    { code: 'ar-SA', name: 'Arabic', flag: 'üá∏üá¶' },
    { code: 'ru-RU', name: 'Russian', flag: 'üá∑üá∫' },
  ];

  // Initialize Speech Recognition
  useEffect(() => {
    const isChrome = /Chrome/.test(navigator.userAgent) && /Google Inc/.test(navigator.vendor);
    const isEdge = /Edg/.test(navigator.userAgent);

    if (!isChrome && !isEdge) {
      setError('‚ö†Ô∏è For best multilingual support, please use Google Chrome or Microsoft Edge.');
    }

    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;

      recognitionRef.current.onresult = async (event) => {
        const speechResult = event.results[0][0].transcript;
        const confidence = event.results[0][0].confidence;
        const detectedLang = selectedLanguage === 'auto' ? (event.results[0][0].lang || 'en-US') : selectedLanguage;

        console.log('Recognized:', speechResult);
        console.log('Language:', detectedLang);
        console.log('Confidence:', confidence);

        setTranscript(speechResult);
        setDetectedLanguage(detectedLang);
        setIsListening(false);

        const userMsg = { role: 'user', content: speechResult, language: detectedLang };
        setMessages(prev => [...prev, userMsg]);

        await getAIResponse(speechResult, detectedLang);
      };

      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setError(`Speech recognition error: ${event.error}`);
        setIsListening(false);
      };

      recognitionRef.current.onend = () => {
        setIsListening(false);
      };
    } else {
      setError('‚ùå Speech recognition not supported. Please use Google Chrome.');
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      synthRef.current.cancel();
    };
  }, [selectedLanguage]);

  const startListening = () => {
    setError('');
    setTranscript('');

    if (recognitionRef.current) {
      try {
        // FIXED: Set the correct language or leave empty for auto-detect
        if (selectedLanguage === 'auto') {
          // For auto-detect, don't set a specific language
          recognitionRef.current.lang = '';
        } else {
          // Set specific language
          recognitionRef.current.lang = selectedLanguage;
        }

        console.log('Starting recognition with language:', selectedLanguage);
        recognitionRef.current.start();
        setIsListening(true);
      } catch (err) {
        setError('Failed to start listening: ' + err.message);
        console.error('Start error:', err);
      }
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

  const getAIResponse = async (userInput, language) => {
    setIsProcessing(true);

    try {
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: userInput,
          language: language
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      const aiResponse = data.response;

      const aiMsg = { role: 'assistant', content: aiResponse, language };
      setMessages(prev => [...prev, aiMsg]);

      await speakText(aiResponse, language);

    } catch (err) {
      console.error('Error:', err);
      setError('Failed to get AI response: ' + err.message);
    } finally {
      setIsProcessing(false);
    }
  };

  const speakText = async (text, language) => {
    return new Promise((resolve) => {
      synthRef.current.cancel();

      const utterance = new SpeechSynthesisUtterance(text);

      const langMap = {
        'en-US': 'en-US',
        'es-ES': 'es-ES',
        'fr-FR': 'fr-FR',
        'de-DE': 'de-DE',
        'it-IT': 'it-IT',
        'pt-BR': 'pt-BR',
        'hi-IN': 'hi-IN',
        'zh-CN': 'zh-CN',
        'ja-JP': 'ja-JP',
        'ko-KR': 'ko-KR',
        'ar-SA': 'ar-SA',
        'ru-RU': 'ru-RU'
      };

      utterance.lang = langMap[language] || language || 'en-US';
      utterance.rate = 0.9;
      utterance.pitch = 1;

      const voices = synthRef.current.getVoices();
      const matchingVoice = voices.find(voice => voice.lang.startsWith(utterance.lang.split('-')[0]));
      if (matchingVoice) {
        utterance.voice = matchingVoice;
      }

      utterance.onstart = () => setIsSpeaking(true);
      utterance.onend = () => {
        setIsSpeaking(false);
        resolve();
      };
      utterance.onerror = () => {
        setIsSpeaking(false);
        resolve();
      };

      synthRef.current.speak(utterance);
    });
  };

  const stopSpeaking = () => {
    synthRef.current.cancel();
    setIsSpeaking(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50 p-4">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-lg p-6 mb-4">
          <div className="flex items-center justify-between flex-wrap gap-4">
            <div className="flex items-center gap-3">
              <Languages className="w-8 h-8 text-purple-600" />
              <div>
                <h1 className="text-2xl font-bold text-gray-800">Multilingual Voice Chatbot</h1>
                <p className="text-sm text-gray-600">Speak in any language - I'll understand and respond</p>
              </div>
            </div>
            <div className="flex items-center gap-3">
              <select
                value={selectedLanguage}
                onChange={(e) => setSelectedLanguage(e.target.value)}
                className="px-4 py-2 border-2 border-purple-300 rounded-lg text-sm font-semibold focus:outline-none focus:border-purple-500"
              >
                {languages.map(lang => (
                  <option key={lang.code} value={lang.code}>
                    {lang.flag} {lang.name}
                  </option>
                ))}
              </select>
              {detectedLanguage && (
                <div className="bg-purple-100 px-3 py-1 rounded-full text-sm text-purple-700">
                  Detected: {detectedLanguage}
                </div>
              )}
            </div>
          </div>
        </div>

        {error && (
          <div className="bg-yellow-50 border-2 border-yellow-300 rounded-lg p-4 mb-4 flex items-start gap-3">
            <AlertCircle className="w-5 h-5 text-yellow-600 flex-shrink-0 mt-0.5" />
            <p className="text-yellow-800 text-sm">{error}</p>
          </div>
        )}

        <div className="bg-white rounded-lg shadow-lg p-8 mb-4">
          <div className="flex flex-col items-center gap-6">
            <button
              onClick={isListening ? stopListening : startListening}
              disabled={isProcessing || isSpeaking}
              className={`w-32 h-32 rounded-full flex items-center justify-center transition-all transform hover:scale-105 ${
                isListening
                  ? 'bg-red-500 hover:bg-red-600 animate-pulse'
                  : 'bg-purple-600 hover:bg-purple-700'
              } ${(isProcessing || isSpeaking) ? 'opacity-50 cursor-not-allowed' : ''} shadow-xl`}
            >
              {isListening ? (
                <MicOff className="w-16 h-16 text-white" />
              ) : (
                <Mic className="w-16 h-16 text-white" />
              )}
            </button>

            <div className="text-center">
              {isListening && (
                <p className="text-lg font-semibold text-purple-600 animate-pulse">
                  üé§ Listening in {languages.find(l => l.code === selectedLanguage)?.name}... Speak now
                </p>
              )}
              {isProcessing && (
                <div className="flex items-center gap-2 text-blue-600">
                  <Loader2 className="w-5 h-5 animate-spin" />
                  <p className="text-lg font-semibold">Processing...</p>
                </div>
              )}
              {isSpeaking && (
                <div className="flex items-center gap-2 text-green-600">
                  <Volume2 className="w-5 h-5 animate-pulse" />
                  <p className="text-lg font-semibold">Speaking...</p>
                  <button
                    onClick={stopSpeaking}
                    className="ml-2 px-3 py-1 bg-red-500 text-white rounded text-sm hover:bg-red-600"
                  >
                    Stop
                  </button>
                </div>
              )}
              {!isListening && !isProcessing && !isSpeaking && (
                <p className="text-gray-600">
                  {selectedLanguage === 'auto' 
                    ? 'Click the microphone and speak in any language'
                    : `Click the microphone to speak in ${languages.find(l => l.code === selectedLanguage)?.name}`
                  }
                </p>
              )}
            </div>

            {transcript && (
              <div className="w-full bg-gray-50 rounded-lg p-4 border-2 border-purple-200">
                <p className="text-sm text-gray-600 mb-1">You said:</p>
                <p className="text-gray-800">{transcript}</p>
              </div>
            )}
          </div>
        </div>

        {messages.length > 0 && (
          <div className="bg-white rounded-lg shadow-lg p-6">
            <div className="flex items-center gap-2 mb-4">
              <MessageSquare className="w-5 h-5 text-purple-600" />
              <h2 className="text-xl font-bold text-gray-800">Conversation</h2>
            </div>
            <div className="space-y-4 max-h-96 overflow-y-auto">
              {messages.map((msg, idx) => (
                <div
                  key={idx}
                  className={`p-4 rounded-lg ${
                    msg.role === 'user'
                      ? 'bg-purple-50 border-l-4 border-purple-500'
                      : 'bg-blue-50 border-l-4 border-blue-500'
                  }`}
                >
                  <div className="flex items-center justify-between mb-2">
                    <span className="font-semibold text-gray-700">
                      {msg.role === 'user' ? 'üë§ You' : 'ü§ñ AI'}
                    </span>
                    <span className="text-xs text-gray-500">{msg.language}</span>
                  </div>
                  <p className="text-gray-800">{msg.content}</p>
                </div>
              ))}
            </div>
          </div>
        )}

        <div className="bg-white rounded-lg shadow-lg p-6 mt-4">
          <h3 className="font-semibold text-gray-800 mb-3">üí° Tips for Best Results</h3>
          <ul className="space-y-2 text-sm text-gray-700">
            <li>‚úÖ Use <strong>Google Chrome</strong> for best multilingual support</li>
            <li>üé§ Allow microphone access when prompted</li>
            <li>üó£Ô∏è Speak clearly and at normal pace</li>
            <li>üåç Select a specific language or use Auto-Detect</li>
            <li>‚è∏Ô∏è Wait for the AI to finish speaking before talking again</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default App;
