import React, { useState, useRef, useEffect } from 'react';
import { Mic, MicOff, Volume2, Loader2, MessageSquare, Sun, CloudRain, Tractor } from 'lucide-react';

const App = () => {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [editedTranscript, setEditedTranscript] = useState('');
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('hi-IN');
  const [detectedLanguage, setDetectedLanguage] = useState('hi-IN');
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState('');
  const [weather, setWeather] = useState(null);

  const recognitionRef = useRef(null);
  const synthRef = useRef(window.speechSynthesis);

  const languages = [
    { code: 'hi-IN', name: 'Select', flag: '' },
    { code: 'hi-IN', name: '‡§π‡§ø‡§Ç‡§¶‡•Ä (‡§ï‡§ø‡§∏‡§æ‡§®)', flag: 'üáÆüá≥' },
    { code: 'en-US', name: 'English', flag: 'üá∫üá∏' },
    { code: 'hi-Hinglish', name: '‡§π‡§ø‡§Ç‡§ó‡•ç‡§≤‡§ø‡§∂', flag: 'üáÆüá≥' },
    { code: 'bn-IN', name: '‡¶¨‡¶æ‡¶Ç‡¶≤‡¶æ (Bengali)', flag: 'üáÆüá≥' },
    { code: 'te-IN', name: '‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å (Telugu)', flag: 'üáÆüá≥' },
    { code: 'ta-IN', name: '‡Æ§‡ÆÆ‡Æø‡Æ¥‡Øç (Tamil)', flag: 'üáÆüá≥' },
    { code: 'mr-IN', name: '‡§Æ‡§∞‡§æ‡§†‡•Ä (Marathi)', flag: 'üáÆüá≥' },
    { code: 'gu-IN', name: '‡™ó‡´Å‡™ú‡™∞‡™æ‡™§‡´Ä (Gujarati)', flag: 'üáÆüá≥' },
    { code: 'kn-IN', name: '‡≤ï‡≤®‡≥ç‡≤®‡≤° (Kannada)', flag: 'üáÆüá≥' },
    { code: 'ml-IN', name: '‡¥Æ‡¥≤‡¥Ø‡¥æ‡¥≥‡¥Ç (Malayalam)', flag: 'üáÆüá≥' },
    { code: 'pa-IN', name: '‡®™‡©∞‡®ú‡®æ‡®¨‡©Ä (Punjabi)', flag: 'üáÆüá≥' }
  ];

  // Load Indore Weather (KissanAI Feature)
  useEffect(() => {
    fetchWeather();
  }, []);

  const fetchWeather = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/weather');
      const data = await res.json();
      setWeather(data);
    } catch {}
  };

  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;
      recognitionRef.current.lang = selectedLanguage;

      recognitionRef.current.onresult = (event) => {
        const speechResult = event.results[0][0].transcript;
        setTranscript(speechResult);
        setEditedTranscript(speechResult);
        setDetectedLanguage(selectedLanguage);
        setIsListening(false);
        setShowConfirmation(true);
      };

      recognitionRef.current.onerror = () => setIsListening(false);
      recognitionRef.current.onend = () => setIsListening(false);
    }
  }, [selectedLanguage]);

  const startListening = () => {
    setError('');
    if (recognitionRef.current) {
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  const handleConfirm = async () => {
    setShowConfirmation(false);
    const userMsg = { role: 'user', content: editedTranscript, language: detectedLanguage };
    setMessages(prev => [...prev, userMsg]);

    await getKisanAIResponse(editedTranscript, detectedLanguage);
  };

  const getKisanAIResponse = async (userInput, language) => {
    setIsProcessing(true);
    try {
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userInput, language })
      });

      const data = await response.json();
      const aiResponse = data.response;

      const aiMsg = { role: 'assistant', content: aiResponse, language, model: data.model };
      setMessages(prev => [...prev, aiMsg]);
      await speakText(aiResponse, language);
    } catch (err) {
      setError('KisanAI ‡§∏‡•á ‡§ú‡§µ‡§æ‡§¨ ‡§®‡§π‡•Ä‡§Ç ‡§Æ‡§ø‡§≤‡§æ: ' + err.message);
    } finally {
      setIsProcessing(false);
    }
  };

  const speakText = async (text, language) => {
    return new Promise((resolve) => {
      synthRef.current.cancel();
      const utterance = new SpeechSynthesisUtterance(text);

      // KissanAI Hindi Optimization
      if (language.includes('hi')) {
        utterance.lang = 'hi-IN';
        utterance.rate = 0.85;  // Slow for farmers
        utterance.pitch = 0.9;
        utterance.volume = 0.9;
      }

      // Hindi Voice Priority
      const voices = synthRef.current.getVoices();
      const hindiVoice = voices.find(v => v.lang.startsWith('hi') || v.name.includes('Hindi'));
      if (hindiVoice) utterance.voice = hindiVoice;

      utterance.onstart = () => setIsSpeaking(true);
      utterance.onend = () => {
        setIsSpeaking(false);
        resolve();
      };

      synthRef.current.speak(utterance);
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 via-yellow-50 to-orange-50 p-4">
      <div className="max-w-2xl mx-auto">
        {/* KissanAI Header */}
        <div className="bg-gradient-to-r from-green-600 to-emerald-600 text-white rounded-2xl p-8 mb-6 shadow-2xl">
          <div className="flex items-center gap-4 mb-4">
            <Tractor className="w-12 h-12" />
            <div>
              <h1 className="text-3xl font-bold">KisanAI</h1>
              <p className="text-green-100">‡§Ü‡§™‡§ï‡§æ ‡§π‡§ø‡§Ç‡§¶‡•Ä ‡§ï‡•É‡§∑‡§ø ‡§∏‡§π‡§æ‡§Ø‡§ï | Indore MP</p>
            </div>
          </div>

          {/* Weather Widget */}
          {weather && (
            <div className="bg-white/20 backdrop-blur-sm rounded-xl p-4">
              <div className="flex items-center gap-3">
                <Sun className="w-6 h-6 text-yellow-300" />
                <div>
                  <p className="font-semibold">{weather.city}: {weather.temp}</p>
                  <p className="text-sm">{weather.condition}</p>
                  <p className="text-xs">{weather.farmingTip}</p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Voice Controls */}
        <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-8 shadow-xl mb-6">
          <div className="flex flex-col items-center gap-6">
            <button
              onClick={isListening ? () => recognitionRef.current.stop() : startListening}
              disabled={isProcessing || isSpeaking || showConfirmation}
              className={`w-28 h-28 rounded-full flex items-center justify-center transition-all transform ${
                isListening
                  ? 'bg-red-500 shadow-red-500/50 animate-pulse'
                  : 'bg-gradient-to-r from-green-500 to-emerald-500 shadow-green-500/50 hover:scale-105'
              } shadow-2xl`}
            >
              {isListening ? (
                <MicOff className="w-12 h-12 text-white" />
              ) : (
                <Mic className="w-12 h-12 text-white" />
              )}
            </button>

            <div className="text-center space-y-2">
              {isListening && (
                <p className="text-xl font-bold text-green-600 animate-pulse">üé§ ‡§¨‡•ã‡§≤‡•ã ‡§≠‡§æ‡§á‡§Ø‡§æ...</p>
              )}
              {isProcessing && (
                <div className="flex items-center gap-2 text-emerald-600">
                  <Loader2 className="w-6 h-6 animate-spin" />
                  <p className="text-lg font-bold">KisanAI ‡§∏‡•ã‡§ö ‡§∞‡§π‡§æ ‡§π‡•à...</p>
                </div>
              )}
              {isSpeaking && (
                <div className="flex items-center gap-2 text-blue-600">
                  <Volume2 className="w-6 h-6 animate-pulse" />
                  <p className="text-lg font-bold">‡§¨‡•ã‡§≤ ‡§∞‡§π‡§æ ‡§π‡•à...</p>
                </div>
              )}
            </div>

            {/* Language Selector */}
            <select
              value={selectedLanguage}
              onChange={(e) => setSelectedLanguage(e.target.value)}
              className="px-6 py-3 bg-white border-2 border-green-200 rounded-xl text-lg font-semibold focus:border-green-500"
              disabled={isListening || isProcessing}
            >
              {languages.map(lang => (
                <option key={lang.code} value={lang.code}>
                  {lang.flag} {lang.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Confirmation Dialog */}
        {showConfirmation && (
          <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl p-8 border-4 border-indigo-200 shadow-2xl mb-6">
            <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
              ‚úèÔ∏è ‡§Ü‡§™‡§®‡•á ‡§ï‡§π‡§æ:
            </h3>
            <textarea
              value={editedTranscript}
              onChange={(e) => setEditedTranscript(e.target.value)}
              className="w-full p-6 border-2 border-gray-300 rounded-xl focus:border-green-500 focus:outline-none text-lg font-medium mb-6 min-h-[100px]"
              placeholder="‡§Ø‡§π‡§æ‡§Å ‡§¨‡§¶‡§≤‡§æ‡§µ ‡§ï‡§∞‡•á‡§Ç..."
            />
            <div className="flex gap-4">
              <button
                onClick={handleConfirm}
                className="flex-1 bg-green-500 hover:bg-green-600 text-white font-bold py-4 px-8 rounded-xl flex items-center justify-center gap-2 shadow-lg transition-all"
              >
                ‚úÖ ‡§≠‡•á‡§ú‡•á‡§Ç KisanAI ‡§ï‡•ã
              </button>
              <button
                onClick={() => setShowConfirmation(false)}
                className="flex-1 bg-gray-500 hover:bg-gray-600 text-white font-bold py-4 px-8 rounded-xl shadow-lg transition-all"
              >
                ‚ùå ‡§∞‡§¶‡•ç‡§¶ ‡§ï‡§∞‡•á‡§Ç
              </button>
            </div>
          </div>
        )}

        {/* Chat History */}
        {messages.length > 0 && (
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-xl max-h-96 overflow-y-auto">
            <h2 className="text-2xl font-bold text-gray-800 mb-4 flex items-center gap-2">
              <MessageSquare className="w-7 h-7 text-green-600" />
              ‡§¨‡§æ‡§§‡§ö‡•Ä‡§§
            </h2>
            <div className="space-y-4">
              {messages.map((msg, idx) => (
                <div
                  key={idx}
                  className={`p-6 rounded-xl shadow-md ${
                    msg.role === 'user'
                      ? 'bg-gradient-to-r from-purple-500 to-indigo-500 text-white border-4 border-purple-200'
                      : 'bg-gradient-to-r from-green-100 to-emerald-100 border-4 border-green-200'
                  }`}
                >
                  <div className="flex items-center justify-between mb-3">
                    <span className="font-bold text-lg">
                      {msg.role === 'user' ? 'üë®‚Äçüåæ ‡§Ü‡§™' : 'ü§ñ KisanAI'}
                    </span>
                    <span className="text-sm bg-white/50 px-3 py-1 rounded-full">
                      {msg.language}
                    </span>
                  </div>
                  <p className="text-lg leading-relaxed">{msg.content}</p>
                  {msg.model && (
                    <p className="text-xs mt-2 opacity-75">Model: {msg.model}</p>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {error && (
          <div className="bg-red-50 border-2 border-red-200 rounded-xl p-6 mt-4">
            <p className="text-red-800 font-semibold">{error}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
