import React, { useState, useRef, useEffect } from 'react';
import { Mic, MicOff } from 'lucide-react';

export default function App() {
  const [isListening, setIsListening] = useState(false);
  const [text, setText] = useState('');
  const [messages, setMessages] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState('hi-IN');
  const [isProcessing, setIsProcessing] = useState(false);

  const recognitionRef = useRef(null);
  const audioRef = useRef(new Audio());
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);


  const languages = [
  { code: 'hi-IN', name: 'à¤¹à¤¿à¤‚à¤¦à¥€ (Hindi)', flag: 'ðŸ‡®ðŸ‡³' },
  { code: 'en-US', name: 'English', flag: 'ðŸ‡ºðŸ‡¸' },
  { code: 'mr-IN', name: 'à¤®à¤°à¤¾à¤ à¥€ (Marathi)', flag: 'ðŸ‡®ðŸ‡³' },
  { code: 'gu-IN', name: 'àª—à«àªœàª°àª¾àª¤à«€ (Gujarati)', flag: 'ðŸ‡®ðŸ‡³' },
  { code: 'bn-IN', name: 'à¦¬à¦¾à¦‚à¦²à¦¾ (Bengali)', flag: 'ðŸ‡®ðŸ‡³' },
  { code: 'te-IN', name: 'à°¤à±†à°²à±à°—à± (Telugu)', flag: 'ðŸ‡®ðŸ‡³' },
  { code: 'ta-IN', name: 'à®¤à®®à®¿à®´à¯ (Tamil)', flag: 'ðŸ‡®ðŸ‡³' },
  { code: 'kn-IN', name: 'à²•à²¨à³à²¨à²¡ (Kannada)', flag: 'ðŸ‡®ðŸ‡³' },
  { code: 'ml-IN', name: 'à´®à´²à´¯à´¾à´³à´‚ (Malayalam)', flag: 'ðŸ‡®ðŸ‡³' },
  { code: 'pa-IN', name: 'à¨ªà©°à¨œà¨¾à¨¬à©€ (Punjabi)', flag: 'ðŸ‡®ðŸ‡³' }
];

  // Initialize Web Speech API
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert('Your browser does not support Speech Recognition');
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = selectedLanguage;

    recognition.onresult = (event) => {
      let interimTranscript = '';
      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcriptSegment = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          setText(prev => transcriptSegment); // final
        } else {
          interimTranscript += transcriptSegment; // live
          setText(interimTranscript);
        }
      }
    };

    recognitionRef.current = recognition;
  }, [selectedLanguage]);

  const startListening = async () => {
  try {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

    const mediaRecorder = new MediaRecorder(stream, {
      mimeType: 'audio/webm;codecs=opus'
    });

    mediaRecorderRef.current = mediaRecorder;
    audioChunksRef.current = [];

    mediaRecorder.ondataavailable = (event) => {
      if (event.data.size > 0) {
        audioChunksRef.current.push(event.data);
      }
    };

    mediaRecorder.start();
    setIsListening(true);
  } catch (err) {
    alert("Microphone permission denied");
    console.error(err);
  }
};

const stopListening = async () => {
  setIsListening(false);

  const recorder = mediaRecorderRef.current;
  if (!recorder) return;

  recorder.stop();

  recorder.onstop = async () => {
    const audioBlob = new Blob(audioChunksRef.current, {
      type: 'audio/webm'
    });

    const base64Audio = await blobToBase64(audioBlob);

    await sendAudioToBackend(base64Audio);
  };
};

  const sendMessage = async () => {
  if (!text.trim()) return;

  const userMsg = { role: 'user', content: text };
  setMessages(prev => [...prev, userMsg]);
  setText('');
  setIsProcessing(true);

  try {
    // Send message with farmer info
    const res = await fetch('http://localhost:8000/voice/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        message: userMsg.content, 
        language: selectedLanguage,
        farmer_id: "farmer_123",  // Replace with real farmer ID from login
        farmer_name: "Ramesh Kumar"  // Replace with real farmer name
      })
    });

    if (!res.ok) {
      throw new Error(`HTTP error! status: ${res.status}`);
    }

    const data = await res.json();
    console.log('âœ… Response:', data);

    const aiMsg = { role: 'assistant', content: data.response };
    setMessages(prev => [...prev, aiMsg]);

    // Play TTS
    const ttsRes = await fetch('http://localhost:8000/voice/tts', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ text: data.response, language: selectedLanguage })
    });

    const ttsData = await ttsRes.json();
    if (!ttsData.audio) {
  throw new Error("TTS audio missing from response");
}

const audioBlob = base64ToBlob(ttsData.audio, 'audio/mp3');

    const audioUrl = URL.createObjectURL(audioBlob);
    audioRef.current.src = audioUrl;
    audioRef.current.play();
    audioRef.current.onended = () => URL.revokeObjectURL(audioUrl);

  } catch (err) {
    console.error('âŒ Full Error:', err);
    alert(`Error: ${err.message}`);
  } finally {
    setIsProcessing(false);
  }
};

const sendChat = async (message, farmerId, farmerName) => {
  const response = await fetch('http://localhost:8000/voice/chat', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      message: message,
      language: 'hi-IN',
      farmer_id: farmerId,      // Use consistent ID
      farmer_name: farmerName
    })
  });

  const data = await response.json();

  // data.response = AI text response
  // data.audio = Base64 audio (play in <audio> tag)
  // data.saved = true/false

  return data;
};

const getHistory = async (farmerId) => {
  const response = await fetch(
    `http://localhost:8000/voice/history/${farmerId}`
  );
  const history = await response.json();
  return history; // Array of chat objects
};

const playAudio = (base64Audio) => {
  const audio = new Audio(`data:audio/mp3;base64,${base64Audio}`);
  audio.play();
};


  const base64ToBlob = (b64, mime) => {
    const bytes = atob(b64);
    const arr = new Uint8Array(bytes.length);
    for (let i = 0; i < bytes.length; i++) arr[i] = bytes.charCodeAt(i);
    return new Blob([arr], { type: mime });
  };

  return (
    <div className="min-h-screen bg-green-50 p-6 flex flex-col items-center">
      <h1 className="text-3xl font-bold mb-6">KisanAI ðŸŒ¾</h1>

      <div className="flex items-center gap-4 mb-4">
        <button
          onClick={isListening ? stopListening : startListening}
          className={`w-16 h-16 rounded-full flex items-center justify-center text-white ${
            isListening ? 'bg-red-500 animate-pulse' : 'bg-green-500 hover:scale-105'
          }`}
        >
          {isListening ? <MicOff /> : <Mic />}
        </button>

        <select value={selectedLanguage} onChange={e => setSelectedLanguage(e.target.value)} className="px-4 py-2 rounded border">
          {languages.map(lang => <option key={lang.code} value={lang.code}>{lang.flag} {lang.name}</option>)}
        </select>
      </div>

      <textarea
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Your speech will appear here..."
        className="w-full max-w-xl p-4 border rounded-lg mb-4 text-lg"
        rows={3}
      />

      <button
        onClick={sendMessage}
        disabled={isProcessing || !text.trim()}
        className="bg-green-500 text-white px-6 py-3 rounded-lg hover:bg-green-600 mb-6"
      >
        Send
      </button>

      <div className="w-full max-w-xl flex flex-col gap-4">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`p-4 rounded-lg ${msg.role === 'user' ? 'bg-purple-200 self-end' : 'bg-green-200 self-start'}`}
          >
            {msg.content}
          </div>
        ))}
      </div>
    </div>
  );
}
